    // 나중에 이 함수로 바꿔야됨
    private fun setUpRv2(rv: RecyclerView){
        //기능 연결할 떄 바꾸기
        val allList = listOf<MainStoreOverView>()
        rv.apply {
            this.adapter = MainStoreOverviewAdapter(applicationContext, allList)
            this.layoutManager = LinearLayoutManager(this@MainActivity, LinearLayoutManager.HORIZONTAL, false)
            this.addOnScrollListener(object : RecyclerView.OnScrollListener(){
                override fun onScrollStateChanged(recyclerView: RecyclerView, newState: Int) {
                    super.onScrollStateChanged(recyclerView, newState)
                    if (newState == RecyclerView.SCROLL_STATE_IDLE) {
                        val firstPos = (rv.layoutManager as LinearLayoutManager).findFirstCompletelyVisibleItemPosition()
                        val secondPos = (rv.layoutManager as LinearLayoutManager).findFirstVisibleItemPosition()
                        val selectedPos = kotlin.math.max(firstPos, secondPos)

                        if (selectedPos != -1) {
                            var viewItem =
                                (rv.layoutManager as LinearLayoutManager).findViewByPosition(selectedPos)
                            viewItem?.run {
                                val itemMargin = (rv.measuredWidth - viewItem.measuredWidth) / 2
                                rv.smoothScrollBy(this.x.toInt() - itemMargin, 0)
                            }
                        }
                    }
                }
            })
        }
    }

