package com.example.yomakase.view.owner

import android.content.Context
import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.databinding.DataBindingUtil
import androidx.navigation.Navigation
import androidx.viewpager2.widget.ViewPager2
import com.example.yomakase.R
import com.example.yomakase.adapter.OwnerAllStoreAdapter
import com.example.yomakase.databinding.FragmentOwnerAllStoreBinding
import com.example.yomakase.model.rv_item.OwnerStoreOverview


class OwnerAllStoreFragment : Fragment() {
    private lateinit var binding: FragmentOwnerAllStoreBinding
    private lateinit var activityContext: Context

    val allUrl ="https://washokulovers.com/wp-content/uploads/2020/12/P1030390.jpg"
    val sushiUrl = "https://static.wixstatic.com/media/ebc3dd_0797491cd9b24205acbbc2c15a5be395~mv2_d_2668_4000_s_4_2.jpg/v1/crop/x_0,y_87,w_2668,h_3826/fill/w_560,h_802,al_c,q_85,usm_0.66_1.00_0.01,enc_auto/_DSC6777-Edit.jpg"
    val umakaseUrl = "data:image/jpeg;base64,"

    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?
    ): View? {
        activityContext = requireContext()
        binding = DataBindingUtil.inflate(inflater, R.layout.fragment_owner_all_store, container, false)

        val itemList = listOf<OwnerStoreOverview>(
            OwnerStoreOverview(allUrl, "가게 이름 1"),
            OwnerStoreOverview(sushiUrl, "가게 이름 2"),
            OwnerStoreOverview(umakaseUrl, "가게 이름 3"),
        )

        binding.vpStore.apply {
            this.adapter = OwnerAllStoreAdapter(itemList, activityContext)
            this.orientation = ViewPager2.ORIENTATION_HORIZONTAL
        }

        binding.indicator.setViewPager(binding.vpStore)
        binding.vpStore.adapter!!.registerAdapterDataObserver(binding.indicator.adapterDataObserver)


        return binding.root
    }
}